import React, { useState, useEffect, } from 'react'
import axios from 'axios'
import './App.css'
import Books from './components/Books'
import Pagination from './components/Pagination'

function App() {
  const [books, setBooks] = useState([])
  const [loading, setLoading] = useState(false)
  const [currentPage, setCurrentPage] = useState(10)
  const [totalBooks, setTotalBooks] = useState()
  const [totalPages, setTotalPages] = useState()

  useEffect(() => {
    const fetchBooks = async () => {
    setLoading(true)
      const res = await axios.get('http://localhost:3000/books?page='+currentPage+'&limit=10')
      setBooks(res.data.results)
      setTotalBooks(res.data.total.books)
      setTotalPages(res.data.total.pages)
      setLoading(false)
    }
    fetchBooks()
    /*
    console.log(totalBooks)
    console.log(totalPages)
    console.log(currentPage)
    console.log(books)
    */
  }, [currentPage])


  function handlePageChange(value){
    if(value === '&laquo;' || value === "... "){
      setCurrentPage(1)
    } else if (value === '&lsaquo;'){
      if (currentPage !== 1) {
        setCurrentPage(currentPage - 1)
      }
    } else if (value === '&rsaquo;'){
      if (currentPage !== totalPages) {
        setCurrentPage(currentPage + 1)
      }
    } else if(value === '&raquo;' || value === " ..."){
      setCurrentPage(totalPages)
    } else {
      setCurrentPage(value)
    }
    console.log(value)
    console.log(currentPage)
  }

  return (
    <div className="container mt-5 ">
      <h1 className='text-primary mb-3 justify-content-center'>Catalogo de Livros</h1>
      <Books books={books} loading={loading} />
      <Pagination totalPage={totalPages} page={currentPage} limit={10} siblings={1} onPageChange={handlePageChange}/>
    </div>
  );
}

export default App;
