import React, { useState, useEffect, /*Pagination*/ } from 'react'
import axios from 'axios'
import './App.css'
import Books from './components/Books'
import { Pagination } from "react-pagination-bar"
import 'react-pagination-bar/dist/index.css'

function App() {
  const [books, setBooks] = useState([])
  const [loading, setLoading] = useState(false)
  const [currentPage, setCurrentPage] = useState(1)
  const [totalPages, setTotalPages] = useState()

  useEffect(() => {
    const fetchBooks = async () => {
      setLoading(true)
      const res = await axios.get('http://localhost:3000/books?page='+currentPage+'&limit=10')
      setBooks(res.data.results)
      setTotalPages(res.data.total.pages)
      setLoading(false)
    }
    fetchBooks()

  }, [])

  return (
    <div className="container mt-5">
      <h1 className='text-primary mb-3'>Catalogo de Livros</h1>
      <Books books={books} loading={loading} />
      <Pagination
        currentPage={currentPage}
        totalItems={totalPages}
        itemsPerPage={10}
        onPageСhange={(pageNumber) => setCurrentPage(pageNumber)}
      />
    </div>
  );
}

export default App;
