import React, { useState, useEffect, /*Pagination*/ } from 'react'
import axios from 'axios'
import './App.css'
import Books from './components/Books'
import ReactPaginate from 'react-paginate';

function App() {
  const [books, setBooks] = useState([])
  const [loading, setLoading] = useState(false)
  const [currentPage, setCurrentPage] = useState(900)
  const [totalBooks, setTotalBooks] = useState()

  useEffect(() => {
    const fetchBooks = async () => {
      setLoading(true)
      const res = await axios.get('http://localhost:3000/books?page='+currentPage+'&limit=10')
      setBooks(res.data.results)
      setTotalBooks(res.data.total.books)
      setLoading(false)
    }
    fetchBooks()
    //console.log(totalBooks)
    //console.log(books)
  }, [])



  const handlePageClick = (event) => {
    setCurrentPage(event.selected)
  }




  return (
    <div className="container mt-5">
      <h1 className='text-primary mb-3'>Catalogo de Livros</h1>

      <Books books={books} loading={loading} />

      <ReactPaginate
        breakLabel="..."
        nextLabel="next >"
        onPageChange={(page) => {setCurrentPage(page)}}
        pageRangeDisplayed={10}
        pageCount={totalBooks/10}
        previousLabel="< previous"
        renderOnZeroPageCount={null}
      />

    </div>
  );
}

ReactDOM.render(
  <PaginatedItems itemsPerPage={10} />,
  document.getElementById('container')
);

export default App;
