import React, { useState, useEffect } from 'react'
import axios from 'axios'
import './App.css'
import Books from './components/Books'
import Pagination from './components/Pagination'

function App() {
  const [books, setBooks] = useState([])
  const [loading, setLoading] = useState(false)
  const [currentPage, setCurrentPage] = useState(1)
  const [booksPerPage, setBooksPerPage] = useState(10)

  useEffect(() => {
    const fetchBooks = async () => {
      setLoading(true)
      const res = await axios.get('http://localhost:3000/books?page='+currentPage+'&limit=1000')
      setBooks(res.data.results)
      setLoading(false)
    }
    fetchBooks()
  }, [])

  //Get current posts
  const indexOfLastBook = currentPage*booksPerPage
  const indexOfFirstBook = indexOfLastBook - booksPerPage
  const currentBooks = books.slice(indexOfFirstBook, indexOfLastBook)


  return (
    <div className="container mt-5">
      <h1 className='text-primary mb-3'>Catalogo de Livros</h1>
      <Books books={/*books*/currentBooks} loading={loading} />
      <Pagination booksPerPage={booksPerPage} totalBooks={books.length} />
    </div>
  );
}

export default App;
